var mysql =require('mysql');
function connectServer(){

    var client=mysql.createConnection({
        host:'localhost',
        user:'root',
        password:'123456',
        database:'new_reading'
    })

    return client;
}

function  selectFun(client,username,callback){
    //client为一个mysql连接对象
    client.query('select passwd from UserTable where username="'+username+'"',function(err,results,fields){
        if(err) throw err;

        callback(results);
    });
}

function insertFun(client , username , passwd,callback){
    client.query('insert into UserTable value(?,?)', [username, passwd], function(err,result){
        if( err ){
            console.log( "error:" + err.message);
            return err;
        }
        callback(err);
    });
}

exports.connect = connectServer;
exports.selectFun  = selectFun;
exports.insertFun = insertFun;
